import './QuestionSlide.scss'
export const QuestionSlide = ({category, incorrectAnswers, correct, question, type}) => {

  const addAnswers = () => {
    const randomIndex = Math.floor(Math.random() * 4)
    return incorrectAnswers.splice(randomIndex, 0, correct);
  }

  const answers = addAnswers();

  return (
    <form className="current-q">
      <h3>{question}</h3>
      <input type="radio" id="opt1" name="option"/>
      <label htmlFor="opt1">{answers[0]}</label> 
      <input type="radio" id="opt2" name="option"/>
      <label htmlFor="opt2">{answers[1]}</label> 
      <input type="radio" id="opt3" name="option"/>
      <label htmlFor="opt3">{answers[2]}</label> 
      <input type="radio" id="opt4" name="option"/>
      <label htmlFor="opt4">{answers[3]}</label> 
      <button onClick={() => answerQuestion()}>Submit Answer</button>
    </form>
  )
}
