import { QuestionSlide } from '../QuestionSlide/QuestionSlide'
import { ScoreBoard } from '../ScoreBoard/ScoreBoard'
import {GrPrevious , GrNext} from 'react-icons/gr'
import './InGame.scss'
import { Component } from 'react'
export class InGame extends Component {
  constructor() {
    super();
    this.state = {
      currentQuestion: {},
      slides: [],
    }
  }


  questionSlides = () => {
    if (this.props.slides) {

      const slides = this.props.slides.map(question => {
        return (
          <QuestionSlide
          category={question.category}
          incorrectAnswers={question.incorrect_answers}
          correct={question.correct_answer}
          question={question.question}
          type={question.type}
          />
          )
        })
        if (slides[0]) this.setState({currentQuestion: slides[0]}); return this.state.currentQuestion
      } else return <div>sorry</div>
  }

      render () {
    return (
      <main className="in-game">
        <GrPrevious/>
        {this.questionSlides()}
        <GrNext/>
        <ScoreBoard/>
      </main>
    )
  }
}
