import './NewGameForm.scss'
import React from 'react'
import { Link } from 'react-router-dom'
export class NewGameForm extends React.Component {

  constructor() {
    super()
    this.state = {
      difficulty: '',
      category: '',
      number: '',
      type: '',
      room: '',
    }
  }

  categoryOptions = () => {
    return this.props.categories.map((category, index) => {
      return <option key={index}>{category.name}</option>
    })
  }

  render () {
    return (
    <form className="new-game">New Game
      <select name="difficulty">
        <option>Difficulty</option>
        <option>Easy</option>
        <option>Medium</option>
        <option>Hard</option>
      </select>
      <select>
        <option>Category</option>
        {this.categoryOptions()}
      </select>
      <input type="number" placeholder="Number of Slides"/>
      <div>
      <input type="radio" name="question-type" id="trueFalse"/>
      <label htmlFor="trueFalse">True / False</label>
      <input type="radio" name="question-type" id="multChoice"/>
      <label htmlFor="multChoice">Multiple Choice</label>
      </div>
      <input placeholder="Room Name"/>
      <Link to="/play">
      <button>Start</button>
      </Link>
    </form>
  )
  }
}
